const orderLinkArray=document.querySelectorAll(".products__button--buy"),modalOrder=document.querySelector(".modal-order"),closeOrder=modalOrder.querySelector(".modal__button-close");for(let e=0;e<orderLinkArray.length;e++)orderLinkArray[e].addEventListener("click",function(e){e.preventDefault(),modalOrder.classList.add("modal-show")});closeOrder.addEventListener("click",function(e){e.preventDefault(),modalOrder.classList.remove("modal-show")}),window.addEventListener("keydown",function(e){27===e.keyCode&&modalOrder.classList.contains("modal-show")&&(e.preventDefault(),modalOrder.classList.remove("modal-show"))});
